import {Card,Header,Message,Grid} from 'semantic-ui-react';
import classes from './Fullpost.css';
const fullpost=(props)=>{
    let fullpost=<Message header="No posts yet" content="Please add a post"></Message>;
        if(props.posts.length>0){
        fullpost=props.posts.map(post=>(
            <div>
            {/* <Card>
                    <Card.Content>
                        <Card.Header>{post.title}</Card.Header>
                        <Card.Description>{post.content}</Card.Description>
                    </Card.Content>
            </Card><br></br> */}
            <Grid.Column>
        <Segment padded>
          <Header>{post.title}</Header>

        </Segment>
      </Grid.Column>
            </div>
        ));
    }
    return(
        <div>

            <Grid columns={3}>
                <Grid.Row>
                {fullpost}

                </Grid.Row> 
                </Grid>




        </div>
    )
}

export default fullpost;
