import React from 'react';
import {Button,TextArea,Modal,Input} from 'semantic-ui-react';
const newpost=(props)=>{
    return(
            <>
                <Modal trigger={<Button>New post</Button>}>
                    <Modal.Header>Add a post</Modal.Header>
                    <Modal.Content>
                    <Modal.Description>
                        <label>TITLE:</label><br></br>
                        <Input name="title" onChange={props.oninputHandler} /><br></br><br></br>
                        <label>DESCRIPTION:</label><br></br>
                        <TextArea rows="4" onChange={props.oninputHandler} name="content" /><br></br><br></br>
                        <Button color="grey" onClick={props.onsaveddata}>Add</Button> 
                    </Modal.Description>
                    </Modal.Content>
                </Modal>
            </>
    )
}

export default newpost;
