import React, { Component } from 'react';

class Auth extends Component{
    state={
        formdata:{
            email: {
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: false,
                touched: false
            },
            password: {
                validation: {
                    required: true,
                    minLength: 6
                },
                valid: false,
                touched: false
            }
        },
        isSignup: true
    }
    render(){

        return(
            <div>
            <input type="email" placeholder="Email" name="email" onChange={this.inputChangeHandler}></input><br />
            <input type="password" placeholder="Password " name="password" onChange={this.inputChangeHandler}></input><br/><br/>
            <button onClick={this.submitHandler}>Submit</button>
            </div>
        )
    }
}



export default Auth;
