import React from 'react';
import {Card,Header,Message,Segment, Container} from 'semantic-ui-react';
import classes from './Fullpost.css';
const fullpost=(props)=>{
    let fullpost=<Message><Container textAlign="center">
        <Message.Header>No posts yet</Message.Header>
        <p>
            Please Add The post
        </p></Container>
        </Message>;
        if(props.posts.length>0){
        fullpost=props.posts.map(post=>(
            <>

            <Card>
                    <Card.Content>
                        <Card.Header>{post.title}</Card.Header>
                        <Card.Description>{post.content}</Card.Description>
                    </Card.Content>
            </Card><br></br>
            </>
        ));
    }
    return(
        <>
            <Segment >
            <Card.Group>
                {fullpost}
            </Card.Group>    
            </Segment>



        </>
    )
}

export default fullpost;
