import React from 'react';
import {Card,Button,Segment, Container} from 'semantic-ui-react';
const Allposts=(props)=>{

    let fullpost=<Container textAlign="center">
        <p>No posts yet</p>
        <p>
            Please Add The post
        </p></Container>
        ;
        if(props.posts.length>0){
        fullpost=props.posts.map(post=>(
            <>
            <Card color="black">
                <Card.Content>
                    <Card.Header>{post.title}</Card.Header>
                    <Card.Description>{post.content}</Card.Description><br></br>
                    <Button onClick={()=>props.deletepost(post.id)} 
                        basic color='red'>
                           Delete
                    </Button>
                </Card.Content>
            </Card><br></br>
            </>
        ));
    }
    return(
        <>
            <Segment >
            <Card.Group>
                {fullpost}
            </Card.Group>    
            </Segment>
        </>
    )
}

export default allPosts;
