import React from 'react';
import {Segment,Header} from 'semantic-ui-react';
const fullpost=(props)=>{
    return(
        <div>
            <Segment>
                <Header>{props.title}</Header>
                <div className="Info">
                    <div className="Author">{props.content}</div>
                </div>
            </Segment>
        </div>
    )
}

export default fullpost;
