import React from 'react';

const post=(props)=>{
    return(
        <div>
                <h1>Add a Post</h1>
                <label>Title</label>
                <input type="text" name="title" onChange={props.oninputHandler} /><br />
                <label>Content</label>
                <textarea rows="4" name="content" onChange={props.oninputHandler} /><br/>
                <button onClick={props.onsaveddata} >Add Post</button>
        </div>
    )
}

export default post;
