import React, { Component } from 'react';
import classes from './Auth.css';
import * as actions from '../../store/actions/index'
import {connect} from 'react-redux';
import {Button,Input, Segment,Header} from 'semantic-ui-react';
class Auth extends Component {
    state = {
        controls: {
            email: {
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: true,
            },
            password: {
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },
                valid: true,  
            }
        },
        isSignup: true
    }



    checkValidity ( value, rules ) {
        let isValid = true;
        if ( !rules ) {
            return true;
        }

        if ( rules.required ) {
            isValid = value.trim() !== '' && isValid;
        }

        if ( rules.minLength ) {
            isValid = value.length >= rules.minLength && isValid
        }

        if ( rules.maxLength ) {
            isValid = value.length <= rules.maxLength && isValid
        }
        if ( rules.isEmail ) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test( value ) && isValid
        }

        console.log(isValid);

        return isValid;
    }

    inputChangedHandler = ( event) => {
        const updatedControls = {
            ...this.state.controls,
            [event.target.name]: {
                ...this.state.controls[event.target.name],
                value: event.target.value,
                valid: this.checkValidity( event.target.value, this.state.controls[event.target.name].validation ),
                touched: true
            }
        };
        this.setState( { controls: updatedControls } );
        console.log(this.state.controls.password.valid);
    }

    submitHandler = ( event ) => {
        event.preventDefault();
        this.props.onAuth( this.state.controls.email.value, this.state.controls.password.value, this.state.isSignup );
    }



    render () {
        const formElementsArray = [];
        for ( let key in this.state.controls ) {
            formElementsArray.push( {
                id: key,
                config: this.state.controls[key]
            } );
        }

        // if (this.props.loading) {
        //     form = <Spinner />
        // }

        let errorMessage = null;

        if (this.props.error) {
            errorMessage = (
                <p>{this.props.error.message}</p>
            );
        }
        let authredirect=null;
        if(this.props.isAuthenticated){
            authredirect=this.props.history.push('/');
        }
        let validationmsg=null;
        if(this.state.controls.email.valid===false || this.state.controls.password.valid===false){
            console.log('hii');
            validationmsg=<p>Email must be in a@gmail.com format and password length must be 6 characters</p>
        }

        return (
            <div className={classes.Auth}>

                {authredirect}
                <Segment textAlign="center" size="big">
                {errorMessage}
                {validationmsg}
                <Header>Enter the login Details</Header>
                <form onSubmit={this.submitHandler}>

                <label>Email:</label><br></br>
                <Input name="email" onChange={this.inputChangedHandler} /><br></br><br></br>
                <label>Password:</label><br></br>
                <Input name="password" onChange={this.inputChangedHandler} /><br></br><br></br>
                <Button color="grey">LOGIN</Button>

                </form>

                </Segment>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        loading: state.loading,
        error: state.error,
        isAuthenticated: state.token !== null
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onAuth: ( email, password, isSignup ) => dispatch( actions.auth( email, password, isSignup ) ),
    };
};

export default connect( mapStateToProps, mapDispatchToProps )( Auth );
