import React, { Component } from 'react';
import {Button,Segment,Input} from 'semantic-ui-react';
class Auth extends Component{
    state={
        formdata:{
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Mail Address'
                },
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: false,
                touched: false
            },
            password: {
                elementType: 'input',
                elementConfig: {
                    type: 'password',
                    placeholder: 'Password'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },
                valid: false,
                touched: false
            }
        },
        isSignup: true
    }
    checkValidity ( value, rules ) {
        let isValid = true;
        if ( !rules ) {
            return true;
        }

        if ( rules.required ) {
            isValid = value.trim() !== '' && isValid;
        }

        if ( rules.minLength ) {
            isValid = value.length >= rules.minLength && isValid
        }

        if ( rules.maxLength ) {
            isValid = value.length <= rules.maxLength && isValid
        }
        if ( rules.isEmail ) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test( value ) && isValid
        }


        return isValid;
    }
    inputChangeHandler=(event)=>{
        const updatedFormdata = {
            ...this.state.formdata,
            [event.target.name]: {
                ...this.state.formdata[event.target.name],
                value: event.target.value,
                valid: this.checkValidity( event.target.value, this.state.formdata[event.target.name].validation ),
                touched: true
            }
        };
        this.setState( { formdata: updatedFormdata} );
    }
    render(){

        return(
            <div>
            <Segment textAlign="center" size="huge">
            <Input type="email" placeholder="Email" name="email" onChange={this.inputChangeHandler}></Input><br />
            <Input type="password" placeholder="Password " name="password" onChange={this.inputChangeHandler}></Input><br/><br/>
            <Button color="blue" >Submit</Button>
            </Segment>
            </div>
        )
    }
}



export default Auth;
