import React, { Component } from 'react';
import Newpost from '../../components/Newpost/Newpost';
import { connect } from 'react-redux';
import Allposts from '../../components/Allposts/Allposts';
import {Menu,Segment,Button, Message, Container} from 'semantic-ui-react';
import {Link,withRouter} from 'react-router-dom';
import Axios from '../../axios-orders';
class blog extends Component{
    state={
        post:{
            title:'',
            content:'',
            userId:''
        },
        fetchedposts:[],
        errorMessage:'null'
    }
    componentDidMount(){
        if(this.props.token){
            const queryParams = '?auth=' + this.props.token + '&orderBy="userId"&equalTo="' + this.props.userid + '"';
            // console.log(queryParams);
            Axios.get('/posts.json' +queryParams)
            .then( res => {
                const fetchedPostsFromServer = [];
                console.log(res.data);
                for ( let key in res.data ) {
                    fetchedPostsFromServer.push( {
                        ...res.data[key],
                        id: key
                    } );
                    this.setState({fetchedposts:fetchedPostsFromServer});
                    // console.log(fetchedOrders);
                    // console.log(this.state.fetchedposts);
                }})
                .catch(err=>{
                    this.setState({errorMessage:err.response.data.error})
                });}
    }

    inputHandler=(event)=>{
        let updatedstate={...this.state.post};
        updatedstate[event.target.name]=event.target.value;
        this.setState({post:updatedstate})
        // console.log(this.state.post);
    }
    saveddata=()=>{
        if(this.props.token){
        const postdata={...this.state.post,userId:this.props.userid};
        // console.log(this.props.token);
        Axios.post('/posts.json?auth=' + this.props.token,postdata)
        .then( response => {
            // console.log( response.data );
            this.setState(prevstate=>(
                prevstate.fetchedposts.push({...postdata,id:response.data.name})
            ));
            // console.log(this.state.fetchedposts);
        } ).catch(err=>{
            // console.log(err.response.data);
            this.setState({errorMessage:err.response.data.error})
        });}
        else{
            this.props.history.push('/login');
        }
    }
    deleteHandler=(id)=>{
        console.log(id);
        Axios.delete(`posts/${id}.json`)
            .then(response =>{
                console.log(response);
                Axios.get('/posts.json')
                .then(response => {

                    // console.log(response);
                    // console.log("userId data...");

                     const fetchedOrders=[];
                     for(let key in response.data) {
                         fetchedOrders.push({
                             ...response.data[key],
                             id:key
                         });
                     }
                     this.setState({fetchedposts:fetchedOrders})
        }
        )})}
    render(){
    return(
        <div>
            <Segment inverted>
            <Menu inverted pointing secondary>
            {this.props.token ?

            // (<Menu.Item position="left">
            //     <Sidebar />
            // </Menu.Item>)
            (<Menu.Item position="right">

                <Button class="ui button" color="grey" as={Link} to ='/'>Posts</Button>
                <Button class="ui button" color="grey" as={Link} to ='/logout'>Log out</Button>

            </Menu.Item>) :
                (<Menu.Item position="right">
                    <Button class="ui button" color="grey" as={Link} to ='/login'>Log In</Button>

                </Menu.Item>)

            }

            </Menu>
            </Segment>
            {this.state.errorMessage?
            <Segment textAlign="center">

            <Newpost oninputHandler={(event)=>this.inputHandler(event)}
            onsaveddata={this.saveddata}/><br></br>
            <Allposts posts={this.state.fetchedposts} deletepost={this.deleteHandler}/>
            </Segment>:<Message><Container textAlign="center">{this.state.errorMessage}</Container></Message>}
        </div>
    );
}
}
const mapStateToProps= state =>{
    console.log(state);
    return{
        token:state.token,
        userid:state.userId
    }
}
export default withRouter(connect(mapStateToProps)(blog));
