import { useBooleanKnob } from '@stardust-ui/docs-components'
import React from 'react'
import {
  Button,
  Grid,
  Header,
  Image,
  Label,
  Menu,
  Segment,
  Sidebar,
} from 'semantic-ui-react'

const initialState = { items: [], count: 0 }

const logReducer = (state, action) => {
  switch (action.type) {
    case 'clearLog':
      return initialState
    case 'updateLog':
      return {
        items: [
          `${new Date().toLocaleTimeString()}: ${action.name}`,
          ...state.items,
        ].slice(0, 20),
        count: state.count + 1,
      }
    default:
      return state
  }
}

const SidebarExampleCallback = () => {
  const [logs, dispatch] = React.useReducer(logReducer, initialState)
  const [visible, setVisible] = useBooleanKnob({ name: 'visible' })

  return (
    <Grid columns={2}>
      <Grid.Column>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation='overlay'
            icon='labeled'
            inverted
            onHidden={() => dispatch({ name: 'onHidden', type: 'updateLog' })}
            onHide={() => {
              setVisible(false)
              dispatch({ name: 'onHide', type: 'updateLog' })
            }}
            onShow={() => dispatch({ name: 'onShow', type: 'updateLog' })}
            onVisible={() => dispatch({ name: 'onVisible', type: 'updateLog' })}
            vertical
            visible={visible}
            width='thin'
          >
            <Menu.Item as='a'>Home</Menu.Item>
            <Menu.Item as='a'>Games</Menu.Item>
            <Menu.Item as='a'>Channels</Menu.Item>
          </Sidebar>

          <Sidebar.Pusher>
            <Segment basic>
              <Header as='h3'>Application Content</Header>
              <Image src='/images/wireframe/paragraph.png' />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Grid.Column>

      <Grid.Column>
        <Segment.Group>
          <Segment>
            <Button
              compact
              size='small'
              floated='right'
              onClick={() => dispatch({ type: 'clearLog' })}
            >
              Clear
            </Button>
            Event Log <Label circular>{logs.count}</Label>
          </Segment>
          <Segment secondary>
            <pre>
              {logs.items.map((e, i) => (
                <div key={i}>{e}</div>
              ))}
            </pre>
          </Segment>
        </Segment.Group>
      </Grid.Column>
    </Grid>
  )
}

export default SidebarExampleCallback
