    //             fetchedOrders.push( {
    //                 ...res.data[key],
    //                 id: key
    //             } );
    //             this.setState({fetchedposts:fetchedOrders});
    //             console.log(fetchedOrders);
    //             console.log(this.state.fetchedposts);
    //         }});
    // }
    inputHandler=(event)=>{
        console.log('hi');
        //console.log(this.state.fetchedposts);
        let updatedstate={...this.state.post};
        updatedstate[event.target.name]=event.target.value;
        this.setState({post:updatedstate})
        console.log(this.state.post);
    }
    saveddata=()=>{
        const postdata={...this.state.post,userId:this.props.userid};
        console.log(this.props.token);
        axios.post('https://blog-app-e7a85.firebaseio.com/posts.json?auth=' + this.props.token,postdata)
        .then( response => {
            console.log( response.data );

        } );
    }
    render(){
        // let fullpost=<p>No posts yet</p>;
        // if(this.state.fetchedposts.length>0){
        // fullpost=this.state.fetchedposts.map(posts=>(
        //     <Fullpost title={posts.title}
        //     content={posts.content}/>
        // ))}
        // console.log(this.state.fetchedposts.length);
    return(
        <div>
            <Segment inverted>
            <Menu inverted pointing secondary>
            {this.props.token ?
            (<Menu.Item position="right">

                <Button class="ui button" color="grey" as={Link} to ='/'>Posts</Button>
                <Button class="ui button" color="grey" as={Link} to ='/login'>Log out</Button>

            </Menu.Item>) :
                (<Menu.Item position="right">
                    <Button class="ui button" color="grey" as={Link} to ='/login'>Log In</Button>

                </Menu.Item>)
            }

            </Menu>
            </Segment>
            <Segment textAlign="center">
            <Newpost oninputHandler={(event)=>this.inputHandler(event)}
            onsaveddata={this.saveddata}/><br></br>
            <Fullpost posts={this.state.fetchedposts} />
            </Segment>
        </div>
    );
}
}
const mapStateToProps= state =>{
    console.log(state);
    return{
        token:state.token,
        userid:state.userId
    }
}
export default connect(mapStateToProps)(blog);
